import './nav-list.css';

let menuList = [
    { id: 1, label: 'Home', link: '/', class: 'active'},
    { id: 2, label: 'About', link: '/about' },
    { id: 3, label: 'Store', link: '/store' },
    { id: 4, label: 'Contacts', link: '/contact' },
    { id: 5, label: 'Where to buy', link: '/foo' }
];

function MenuItem({label, link, isActive}) {
    return (
        <li>
            <a className={`pages-link ${isActive ? 'active' : ''}`} href={link}>{label}</a>
        </li>
    )
}

export default function myNavListComp() {
    return (
        <nav>
            <ul className="pages-list">
                {menuList.map(item => (
                    <MenuItem 
                        key={item.id} 
                        label={item.label} 
                        link={item.link} 
                        isActive={item.class === 'active'}
                    />
                ))}
            </ul>
        </nav>
    )
}
