import './nav-list.css';
import NavList from '../common/nav-list';

const menuList = [
    { id: 1, label: 'Home', link: '/', class: 'active'},
    { id: 2, label: 'About', link: '/about' },
    { id: 3, label: 'Store', link: '/store' },
    { id: 4, label: 'Contacts', link: '/contact' },
    { id: 5, label: 'Where to buy', link: '/foo' }
];

function renderMenuItem(item) {
    return (
        <a className={`pages-link ${item.class === 'active' ? 'active' : ''}`} href={item.link}>{item.label}</a>
    );
}

export default function MyNavListComp() {
    return (
        <nav>
            <NavList items={menuList} className="pages-list" itemRenderer={renderMenuItem} />
        </nav>
    );
}
