import React from 'react';
import NavList from './NavList'; // Путь к вашему универсальному компоненту

const elemNavigation = [
    {id: 1, class: 'nav-home', value: 'Home', link: '/'},
    {id: 2, class: 'nav-contacts', value: 'Contacts', link: '/contacts'},
    {id: 3, class: 'nav-call', value: 'Call me', link: '/call'},
    {id: 4, class: 'nav-store', value: 'Store', link: '/store'},
    {id: 5, class: 'nav-support', value: 'Support', link: '/support'},
];

function renderNavigationItem(item) {
    return (
        <a className={`${item.class} aside-links`} href={item.link}>{item.value}</a>
    );
}

export default function Navigation() {
    return (
        <aside className="navigation">
            <NavList items={elemNavigation} className="list-icons" itemRenderer={renderNavigationItem} />
        </aside>
    );
}
